  [106589] = "{id=106589,Text='结盟的羽翼永远丰盈！迪文，随时听命！',Speaker=7730,}",
  [106617] = "{id=106617,Text='结盟的羽翼永远丰盈！迪文，随时听命！',Speaker=7758,}",
  [106700] = "{id=106700,Text='在危机面前，不分国与国！',Speaker=7757,}",
  [106701] = "{id=106701,Text='秘密之翼受卡尔之命前来支援。',Speaker=7757,}",
  [106702] = "{id=106702,Text='年轻人，我就在等你这一句话！',Speaker=7753,}",
