  [655244] = "{id=655244,Text='她希望用这种方式，鼓励学生和克鲁鲁战斗，并不断成长、变强呢喵。',Speaker=816564,Option='原来如此',}",
  [655245] = "{id=655245,Text='这些年来喵，很多人都试着挑战克鲁鲁，可惜到现在还没有人成功喵。',Speaker=816564,}",
  [655246] = "{id=655246,Text='但换做你的话，没准就能打败它了喵！',Speaker=816564,}",
  [655247] = "{id=655247,Text='[c][ffff00][PlayerName][-][/c]，有兴趣为守护学院出一份力喵？【[c][ffff00]以下为技能教学副本，请选择是否接受[-][/c]。】',Speaker=816564,Option='{是 ,1100},{否,1003}',}",
  [655248] = "{id=655248,Text='我还要更重要的事。',}",
  [655249] = "{id=655249,Text='嗯，可以理解喵。这个机会就留给你的学弟学妹们吧喵。',Speaker=816564,}",
  [655250] = "{id=655250,Text='现在，你可以找希格德莉法办理离校手续了喵。',Speaker=816564,}",
  [655251] = "{id=655251,Text='需要我把你传送过去喵？',Speaker=816564,Option='{需要,1004},{不需要,1005}',}",
  [655252] = "{id=655252,Text='我想挑战克鲁鲁，获得“勇者印记”！',}",
  [655253] = "{id=655253,Text='咪呜~我就知道你不会拒绝的喵。',Speaker=816564,}",
  [655254] = "{id=655254,Text='克鲁鲁就在星泪森林的深处喵，我会用魔法将你传送过去的。',Speaker=816564,}",
  [655255] = "{id=655255,Text='武器和装备都穿戴好了吗？要出发咯喵？',Speaker=816564,Option='{没问题了,1102},{再等一下,1101}',}",
  [655256] = "{id=655256,Text='武器和装备都穿戴好了吗？要出发咯喵？',Speaker=816564,Option='{没问题了,1104},{再等一下,1103}',}",
  [655257] = "{id=655257,Text='[c][ffff00][PlayerName][-][/c]，有兴趣为守护学院出一份力喵？【[c][ffff00]以下为技能教学副本，请选择是否接受[-][/c]。】',Speaker=816564,Option='{是 ,2100},{否,2003}',}",
  [655258] = "{id=655258,Text='需要我把你传送过去喵？',Speaker=816564,Option='{需要,2004},{不需要,2005}',}",
  [655259] = "{id=655259,Text='武器和装备都穿戴好了吗？要出发咯喵？',Speaker=816564,Option='{没问题了,2102},{再等一下,2101}',}",
  [655260] = "{id=655260,Text='武器和装备都穿戴好了吗？要出发咯喵？',Speaker=816564,Option='{没问题了,2104},{再等一下,2103}',}",
  [655261] = "{id=655261,Text='咪呜……教学应该结束了吧~但总觉得少了什么喵……',Speaker=816564,Emoji=19,}",
  [655262] = "{id=655262,Text='啊，我想起来啦——你还没有合身的装备喵！',Speaker=816564,Option='{说到装备…… ,0},{我想起来了！,0}',}",
  [655263] = "{id=655263,Text='咪呜？阿历克塞和你打赌，把那身“英灵护甲”输给你了？',Speaker=816564,}",
  [655264] = "{id=655264,Text='哎呀呀，他还真是个糊涂鬼呢！',Speaker=816564,Option='英灵护甲？',Emoji=17,}",
  [655265] = "{id=655265,Text='那是女武神给的东西！很神奇的喵，会自己改变样式哦喵~',Speaker=816564,}",
  [655266] = "{id=655266,Text='去找阿历，让他履行自己的赌约喵！',Speaker=816564,}",
  [655267] = "{id=655267,Text='要我送你一程喵？',Speaker=816564,Option='好',}",
  [655268] = "{id=655268,Text='好，那我现在就将你转职成唤灵者喵。',Speaker=816564,ModelFace=1,ModelAct=867,Action={actionid=203},}",
  [655269] = "{id=655269,Text='你已经拥有该系的三转职业了，请把这次机会留给[c][ffff00]其他职业[-][/c]吧喵。',Speaker=816564,}",
  [655270] = "{id=655270,Text='不如把这个新生交给我吧！她的身上有股神奇的力量，闻起来就和烤小鸟一样香~',Speaker=811921,}",
  [655271] = "{id=655271,Text='是命运指引你我在此相遇，愿你成长为肩负世界之人。',Speaker=811888,ModelFace=6,ModelAct=867,BlackMask=1,}",
  [655273] = "{id=655273,Text='你好，冒险者！最近收到罗扎纳附近频繁发生地震的报告，这很反常……',Speaker=7042,}",
  [655274] = "{id=655274,Text='可不可以请你去调查一下，看看究竟有什么事发生！',Speaker=7042,Option='{我这就去,3},{没有时间,2}',}",
  [655275] = "{id=655275,Text='没有关系，如果你改变主意的话可以随时来找我！',Speaker=7042,}",
  [655276] = "{id=655276,Text='我们派遣了学识渊博的多兰族调查员协助你。',Speaker=7042,}",
  [655277] = "{id=655277,Text='他叫狄德罗·雪耳，已经先行前往罗比奥森林了。',Speaker=7042,}",
  [655278] = "{id=655278,Text='去那里与他汇合吧！',Speaker=7042,Option='{立刻就去,5},{稍后再去,4}',}",
  [655279] = "{id=655279,Text='这地震……这是太奇怪了喵……',Speaker=811957,Option='怎么了？',}",
  [655280] = "{id=655280,Text='啊！是你啊冒险者……我想问题想得太入神了喵，没注意到你来了喵。',Speaker=811957,Emoji=7,}",
  [655281] = "{id=655281,Text='最近发生的地震，实在是太可疑了喵！',Speaker=811957,Option='疑点',}",
  [655282] = "{id=655282,Text='罗扎纳很少有地震发生，像这样连续的地震更是前所未有的事喵，而且……',Speaker=811957,}",
  [655283] = "{id=655283,Text='根据我的调查，附近的居民在地震发生时，都听到了凄厉的哭声喵……',Speaker=811957,}",
  [655284] = "{id=655284,Text='我怀疑——一定是有什么强大的魔物在搞鬼喵……',Speaker=811957,}",
  [655285] = "{id=655285,Text='这是一部地震探测装置，可以探测出地震的源头喵……',Speaker=811957,}",
  [655286] = "{id=655286,Text='不过因为是临时组装的喵，不是特别灵敏，只能探测出大致的方向……',Speaker=811957,}",
  [655287] = "{id=655287,Text='啊……又开始地震了喵，快看看探测的结果！',Speaker=811957,}",
  [655288] = "{id=655288,Text='（探测装置发出“嘀嘀”的响声，指针指向北面）',}",
  [655289] = "{id=655289,Text='看来地震的源头在北面，快去调查一下喵！',Speaker=811957,}",
  [655290] = "{id=655290,Text='（附近看起来没有什么特别的）',}",
  [655291] = "{id=655291,Text='（探测装置发出“嘀嘀”的响声，指针指向东面）',}",
  [655292] = "{id=655292,Text='（那是？）',}",
  [655293] = "{id=655293,Text='是入侵者喵！',Speaker=811959,Emoji=7,}",
  [655294] = "{id=655294,Text='他想要抢走我们的圣物喵！',Speaker=811960,}",
  [655295] = "{id=655295,Text='杀了他！喵喵~~',Speaker=811961,}",
  [655296] = "{id=655296,Text='那株植物是什么？群叶猫为什么说它是圣物？',}",
  [655297] = "{id=655297,Text='（从神秘植物的花朵中传出响彻天地的痛哭声……）',}",
  [655298] = "{id=655298,Text='（夹杂在哭声中，地震探测装置“嘀嘀”响个不停，这里就是地震的源头……）',}",
  [655299] = "{id=655299,Text='（难道这株植物就是引发地震的元凶吗？！）',}",
  [655300] = "{id=655300,Text='（伴随着大地的震颤与悲戚的哭声，将神秘的植物连根拔起）',}",
  [655301] = "{id=655301,Text='（手中的植物十分沉重，似乎有一股力量要将你拽向地面，令你难以移动）',}",
  [655302] = "{id=655302,Text='（将这株植物带给狄德罗看看吧，或许他知道这是什么！）',}",
  [655303] = "{id=655303,Text='……巴德尔…………巴德尔………………',Speaker=811965,}",
  [655304] = "{id=655304,Text='（随着魔物被击退，你感到神秘植物变轻了……）',}",
  [655305] = "{id=655305,Text='冒险者，你回来了喵！有什么发现吗喵？',Speaker=811957,Option='转告经历',}",
  [655306] = "{id=655306,Text='一株神秘的植物吗？给我看看喵~',Speaker=811957,Option='将植物递给他',}",
  [655307] = "{id=655307,Text='喵？这是——槲寄生？',Speaker=811957,Emoji=19,}",
  [655308] = "{id=655308,Text='如果是槲寄生的话，会不会……',Speaker=811957,}",
  [655309] = "{id=655309,Text='巴德尔……巴德尔……我的孩子…………',Speaker=811966,Emoji=42,}",
  [655310] = "{id=655310,Text='原来……是这样喵！我明白了喵！',Speaker=811957,}",
  [655311] = "{id=655311,Text='原来是这样喵……',Speaker=811957,Option='怎么回事？',}",
  [655312] = "{id=655312,Text='这颗槲寄生，就是刺死巴德尔的植物啊喵！',Speaker=811957,Option='巴德尔？',Emoji=7,}",
  [655313] = "{id=655313,Text='巴德尔是奥丁与天后弗丽嘉的儿子喵，他英俊、高洁，受到所有人的喜爱喵……',Speaker=811957,}",
  [655314] = "{id=655314,Text='弗丽嘉让世间万物许下承诺，不会伤害巴德尔——除了墙角的槲寄生喵……',Speaker=811957,}",
  [655315] = "{id=655315,Text='它太过弱小，以至于天后并不认为它能够伤害巴德尔喵……',Speaker=811957,Option='然后呢？',}",
  [655316] = "{id=655316,Text='然后？巴德尔最终被一株小小的槲寄生刺死喵——这是这个世界上唯一能杀死他的东西喵。',Speaker=811957,Emoji=25,}",
  [655317] = "{id=655317,Text='弗丽嘉遭受丧子之痛，她的怨念也随着巴德尔的血迹，寄居在这株植物中了喵……',Speaker=811957,}",
  [655318] = "{id=655318,Text='当槲寄生开花时，弗丽嘉的怨念便释放了出来喵~',Speaker=811957,}",
  [655319] = "{id=655319,Text='作为掌管天空与大地的女神，她的神力引发了连续的地震喵！',Speaker=811957,}",
  [655320] = "{id=655320,Text='这一切就能说得通了喵！',Speaker=811957,Option='如何处置？',Emoji=11,}",
  [655321] = "{id=655321,Text='喵呜……这倒是个难题呀……',Speaker=811957,Emoji=15,}",
  [655322] = "{id=655322,Text='这样蕴含神力的东西如果处置不当，可是会有大麻烦的喵……',Speaker=811957,}",
  [655323] = "{id=655323,Text='有办法了喵！',Speaker=811957,}",
  [655324] = "{id=655324,Text='就把它改造成一件武器吧喵！',Speaker=811957,}",
  [655325] = "{id=655325,Text='这种力量，作为武器再合适不过了喵！而且——',Speaker=811957,}",
  [655326] = "{id=655326,Text='我相信你一定能够将这种力量用于正途的喵！',Speaker=811957,}",
  [655327] = "{id=655327,Text='完成了喵！',Speaker=811957,}",
  [655328] = "{id=655328,Text='哇！真是了不起的力量喵——要好好利用它哦！',Speaker=811957,}",
  [655329] = "{id=655329,Text='刚刚传来消息，最近天津町频频出现魔物袭击人类的事件。',Speaker=7042,}",
  [655330] = "{id=655330,Text='冒险者，可以去调查一下，看看究竟发生了什么吗？',Speaker=7042,Option='{我这就去,3},{没有时间,2}',}",
  [655331] = "{id=655331,Text='原来是这样吗，那只能作罢了。如果你改变主意的话可以随时来找我！',Speaker=7042,}",
  [655332] = "{id=655332,Text='天津町的汤浅婆婆前些日子遭受了魔物的袭击，不如先去找她了解一下情况吧！',Speaker=7042,}",
  [655333] = "{id=655333,Text='你好啊，冒险者，找我有什么事吗？',Speaker=811972,Option='关于袭击',}",
  [655334] = "{id=655334,Text='唉……谁能想到一向太平的天津町会出这种事呢……',Speaker=811972,Emoji=15,}",
  [655335] = "{id=655335,Text='最近突然冒出个自称祸津大王的魔头，纠集了一群魔物袭击居民，给大家造成了不少麻烦……',Speaker=811972,}",
  [655336] = "{id=655336,Text='巫女大人也组织人手前去剿灭——但那个祸津大王一看到我们人多势众，就带着手下逃走了……',Speaker=811972,}",
  [655337] = "{id=655337,Text='没几天又回来，变本加厉的骚扰居民……真是不知道怎么办才好！',Speaker=811972,Option='祸津大王的行踪',}",
  [655338] = "{id=655338,Text='那个魔头经常出没在天津町东面——诶，你问这个做什么？你不会是想一个人对付那个魔头吧？',Speaker=811972,}",
  [655339] = "{id=655339,Text='那可太危险了！',Speaker=811972,Option='{我很强的,0},{我不害怕,0}',}",
  [656453] = "{id=656453,Text='另外，你也要记住：职业流派之间除了武器不同，所需要的素质点也不尽相同。',Speaker=824436,}",
  [662739] = "{id=662739,Text='我想我没有办法继续拯救这个世界的不幸了。',Speaker=829288,}",
  [662740] = "{id=662740,Text='请将这封信交给我的丈夫……再见了。',Speaker=829288,Option='埃利诺！',}",
  [662741] = "{id=662741,Text='……',Speaker=829288,}",
  [662742] = "{id=662742,Text='这是埃利诺的信，她在骑士团还好吗？',Speaker=829291,Option='她牺牲了',}",
  [662743] = "{id=662743,Text='她在哪儿！！我要去找她！',Speaker=829291,}",
  [662744] = "{id=662744,Text='埃利诺……我的埃利诺……',Speaker=829291,}",
  [662745] = "{id=662745,Text='……',Speaker=829288,}",
  [662746] = "{id=662746,Text='请帮我安葬她吧……谢谢你带我见到她最后一面。',Speaker=829291,}",
  [662747] = "{id=662747,Text='我看了埃利诺的信，原来她一直记得我对她说的话。',Speaker=829291,}",
  [662748] = "{id=662748,Text='“这个世界已经卷入了不幸的漩涡中，那就用你的力量减少更多的不幸吧！”',Speaker=829291,}",
  [662749] = "{id=662749,Text='我想如果不是我和她说这句话，她或许不会就这样死去。',Speaker=829291,}",
  [662750] = "{id=662750,Text='我突然意识到这句话最终却成为了她的枷锁……她或许应该有着不一样的结局。',Speaker=829291,}",
  [662751] = "{id=662751,Text='再见了冒险者，我会在这里守护埃利诺，直到看到这个世界的不幸消散。',Speaker=829291,}",
  [662752] = "{id=662752,Text='终于见到你了！',Speaker=829293,}",
  [662753] = "{id=662753,Text='如果决斗是必然的，那就让决斗变得有趣起来！',Speaker=829293,}",
  [662754] = "{id=662754,Text='输者要说一个秘密！',Speaker=829293,Option='赞同',}",
  [662755] = "{id=662755,Text='没想到是说我输了……嗯……说点什么好呢？',Speaker=829293,}",
  [662756] = "{id=662756,Text='其实我并不是女孩子！！骗你的啦……卡利斯格没有秘密！我们是永恒的骄傲与花朵。',Speaker=829293,}",
  [662757] = "{id=662757,Text='那就好！呼……请把她们的剑送往遗落花地埋葬了吧。',Speaker=829237,}",
  [662759] = "{id=662759,Text='……',Speaker=829237,}",
  [662760] = "{id=662760,Text='……',Speaker=829248,}",
  [662761] = "{id=662761,Text='……',Speaker=829339,}",
  [662762] = "{id=662762,Text='……',Speaker=829266,}",
  [662763] = "{id=662763,Text='……',Speaker=829273,}",
  [662764] = "{id=662764,Text='……',Speaker=829277,}",
  [662765] = "{id=662765,Text='……',Speaker=829288,}",
  [787658] = "{id=787658,Text='这片龙血浸透的战场，渴求新英雄的降临。接下来你要面对的战斗，我将为你详尽阐释。',Speaker=813602,Option='{清剿堕龙,2},{断绝孽种,3},{歼灭魔物,4},{解救俘虏,5},{破坏祭坛,6},{离开,7}',}",
  [787659] = "{id=787659,Text='堕龙祭司正在扩大时空乱流的裂隙，试图将深渊之湖也拉入乱流之中，请务必终止堕落龙族的邪恶行径！',Speaker=813602,}",
  [787660] = "{id=787660,Text='堕落的龙族正在四处孵化黑暗军团，为断绝邪恶的延续，请您摧毁它们。',Speaker=813602,}",
  [787661] = "{id=787661,Text='异界魔物计划通过时空矩阵入侵深渊秘境，请您尽力阻止这些致命时空垃圾。',Speaker=813602,}",
  [787662] = "{id=787662,Text='侦查到我军战士被囚禁在牢笼中！他们的生命力正在被抽取，拯救行动刻不容缓！',Speaker=813602,}",
  [787663] = "{id=787663,Text='邪恶力量正在瓦解我军的精神防线 —— 必须彻底摧毁这些堕龙守卫的邪恶祭坛！',Speaker=813602,}",
  [1312167] = "{id=1312167,Text='真羡慕年轻人的活力啊',Speaker=9901,}",
  [1312168] = "{id=1312168,Text='拉姆，那个人曾经在下水道帮助过的我',Speaker=9902,}",
  [1312169] = "{id=1312169,Text='我也认识他，很厉害的冒险者',Speaker=9903,}",
  [1312172] = "{id=1312172,Text='是[c][ffff00][PlayerName][-][/c]，好久没在城里见到你了呢～',Speaker=9910,}",
  [1312173] = "{id=1312173,Text='我在依斯鲁得见过他呢，那双清澈的眼神看起来就很让人羡慕呢',Speaker=9911,}",
  [1312174] = "{id=1312174,Text='我的宝藏！去哪了？',Speaker=9913,Emoji=19,}",
  [1312189] = "{id=1312189,Text='嘿嘿，你最近话好像变多了…',Speaker=9942,}",
  [1312190] = "{id=1312190,Text='…………',Speaker=9943,}",
  [1312191] = "{id=1312191,Text='那小子…真的能击败巴风特吗…',Speaker=9948,}",
  [1312192] = "{id=1312192,Text='我可没有给他什么秘密武器，艾尔克',Speaker=9947,}",
  [1312193] = "{id=1312193,Text='能遇到你是我的荣幸，冒险者',Speaker=9949,}",
  [1312194] = "{id=1312194,Text='感谢你冒险者，你的勇气拯救了世界！',Speaker=9952,}",
  [1312195] = "{id=1312195,Text='感谢你冒险者，你的勇气值得尊敬！',Speaker=9950,}"
}
